package com.syntax.class01;

public class DataTypesAndVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//primative numeric datatypes 
		byte variable1=4;
		short variable2=100;
		int variable3=1000;
		long variable4=10000000; // if numbers too big put l or L at end
		//to store floating numbers
		// add f at end for float datatype
		float var5=12.99f;
		double var6=9.99999;
		//represents yes or no 
		boolean vars7=true;
		boolean vars8=false;
		//to represent single character
		char vars9='K';
		//to show value of data stored
				System.out.println(vars9);
		}

}
