package com.syntax.class01;

public class PracticeVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte small=127;//numbers -127 to 127
		short decent=32767;//max is 32767
		int bigger=214748364;//bigger number data storage but not biggest
		long longest=1000000000000L;//if number is too big needs L at end
		float decimal=1.75f;//fractions needs f at end
		double decimals=2.7777;//fractions
		char character='k';//single character
		boolean statement=true;//true or false
		System.out.println(decent);
		System.out.println(small);
		System.out.println(bigger);
		System.out.println(longest);
		System.out.println(decimal);
		System.out.println(decimals);
		System.out.println(character);
		System.out.println(statement);
		
	}

}
