package com.syntax.class02;

public class Arithemetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Num, Num2;
		
		Num=10;
		Num2=12;
		
		System.out.println(Num+Num2);
		
		//Arithemetic operations +,-,*,/,%
		
		System.out.println(Num*Num2);
		
		int Multi=Num+Num2;
		
		System.out.println(Multi/2);
		
		float f=19.99f;
		float f1=2.99f;
		System.out.println("the result will be "+f/f1);
		//difference between double and float are the decimals spaces 
		//float will hold 5 to 6 numbers
		//double will hold 14 to 15 numbers
		
		
	}

}
